text:
.name-list {
  width: 100%;
  tr:nth-child(even) td {background: #ffffec;}
  th {text-align: left;}
}
.postcard-script {
  padding: 1em;
}
.claimant-print {display: none;}
@media print {
  .tc-subtitle, .tc-tags-wrapper {display: none;}
  .tc-tagged-PostcardGroup {break-after: page;}
  p:has(.tc-drafts-list),  .story-frontdrop,.tc-alerts, #styleArea {display: none;}
  .claimant-web {display: none;}
  .claimant-print {display: block; text-align: right; font-size: 150%;}
}
